package Oct19P1;

public class MyProgram7 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int i;
		for(i= 1;i <=100 ;i=i*4) {
			System.out.println("the i is in first loop" + i);
		}
		
		for(i = 1000;i >100 ;i=i-200) {
			System.out.println("the count is in second loop" + i);
		}
		for(i = 100;i <10 ;i--) {
			System.out.println("the count is in third loop" + i);
		}
	}

}
